INSERT INTO comic (title, synopsis, page_count, date, image, creator, price, rarity) VALUES ('Senninha e Sua Turma #7', 'Senninha e seus amigos vão fazer uma viagem de férias para a fazenda. Lá eles vão aprender sobre os animais, plantações e como cuidar da natureza. Eles também vão enfrentar desafios e viver aventuras no campo. É a história “Aventuras na fazenda”. Além disso, tem a historinha “O show de talentos”, em que Senninha e sua turma participam de um concurso de talentos.', 36, '03/11/2015', 'https://dvygmnfdru1l.cloudfront.net/Uo4mMNdEzTLp2FyTnBC2/7/53d6a87a-6fd9-4099-a872-976daeffe532', 'Mauricio de Sousa', 45.00 , '1');

