INSERT INTO comic (title, synopsis, page_count, date, image, creator, price, rarity) VALUES ('Senninha e Sua Turma #8', 'Senninha e seus amigos vão participar de um torneio de futebol. Eles vão treinar bastante para se preparar para os jogos e enfrentarão times de outras escolas. Além do futebol, também vão enfrentar desafios e aprender importantes lições sobre trabalho em equipe e superação. É a história “O torneio de futebol”. Além disso, tem a historinha “A máquina do tempo”, em que Senninha faz uma viagem no tempo e conhece personagens históricos.', 36, '03/11/2015', 'https://dvygmnfdru1l.cloudfront.net/Uo4mMNdEzTLp2FyTnBC2/8/53d6a87a-6fd9-4099-a872-976daeffe532', 'Mauricio de Sousa', 50.00 , '1');
